import React, { useState, useEffect } from "react";
import {
  AutoComplete,
  Row,
  Col,
  Form,
  Input,
  Button,
  Spin,
  notification,
  Switch,
  Select
} from "antd";
import "./../../App.css";
import axios from "axios";
import outputNLP from "./../../data/outputNLP.json";

const AddArticle = () => {

  const initialFormState = {
    raname: "",
    articleNo: "",
    articleName: "",
    customText: "",
  };

  const formItemLayout = {};
  const { TextArea } = Input;
  const { Option } = Select;
  const [loading, setLoading] = useState(false);
  const [input, setInput] = useState({ value: true });
  let userDetails = JSON.parse(localStorage.getItem('user'));
  const [addArticle, setAddArticle] = useState(initialFormState);


  const [selectedArticles, setselectedArticles] = useState({ articles: [] });
  function handleChange(e) {
    addArticle.raname = e;
    setAddArticle(prevValue => ({ ...prevValue, raname: e }));
    let new_e = outputNLP.find(item => {
      return item.hint === e;
    })
    setselectedArticles(new_e)
    console.log(selectedArticles);
  }

  const [formVariables, setFormVariables] = useState([{ key: "", value: "" }])
  const handleVariablesChange = (i, e) => {
    let newFormVariables = [...formVariables];
    newFormVariables[i][e.target.name] = e.target.value;
    setFormVariables(newFormVariables);
  }

  const openNotificationWithIcon = (type, title, description) => {
    notification[type]({
      message: title,
      description: description,
    });
  };

  const [selectedArticle, setSelectedArticle] = useState({ variables: [] });
  const onChange = (value) => {
    setAddArticle({ ...addArticle, articleName: value })
    addArticle.articleName = value;
    outputNLP.map(item => {
      item.articles.map(data => {
        if (data.article == value) {
          setAddArticle({ ...addArticle, articleNo: data.id })
          addArticle.articleNo = data.id;
          setSelectedArticle(data)
        }
      })
    })
  };

  function handleChangeRA(e) {
    const value = e.target.value;
    setAddArticle({
      ...addArticle,
      [e.target.name]: value
    });
  }

  const handleInput = (e) => {
    input.value = !e;
    setInput(prevValue => ({ ...prevValue, value: !e }));
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true)
    const jwtToken = localStorage.getItem("token");
    axios
      .post(`http://${process.env.REACT_APP_GATEWAY_HOST}:${process.env.REACT_APP_GATEWAY_PORT}/proposeAddArticle`, { addArticle, userDetails }, { headers: { "Authorization": `Bearer ${jwtToken}` } })
      .then((res) => {
        if (res.status === 200) {
          openNotificationWithIcon(
            "success",
            "SUCCESSFULLY REGISTERED AGREEMENT"
          );
        }
        if (res.status === 201) {
          openNotificationWithIcon(
            "error",
            "MISSING VALUES TO CREATE THE AGREEMENT"
          );
        }
        if (res.status === 202) {
          openNotificationWithIcon(
            "error",
            "ROAMING AGREEMENT MUST BE CREATED BETWEEN TWO MNOs"
          );
        }
      })
      .catch(() =>
        openNotificationWithIcon(
          "error",
          "UNREGISTERED ROAMING AGREEMENT",
        )
      )
      .finally(() => setLoading(false));
  };

  return (
    <section className="CommentsWrapper">
      <h2> PROPOSE ARTICLES FOR ROAMING AGREEMENT</h2>
      <Row gutter={[16, 16]} type="flex">
        <Col xl={24} lg={24} md={24}>
          <Form
            {...formItemLayout}
            name="basic"
            initialvalues={{
              remember: true
            }}
            onSubmit={handleSubmit}
          >
            <Spin spinning={loading}>
              <Col lg={1} md={24}></Col>
              <Col lg={23} md={24}>
                <Form.Item hasFeedback>
                  <Row>
                    <Form.Item label="NAME OF THE ROAMING AGREEMENT">
                      <Select
                        size="large"
                        placeholder={"E.g.: RA001"}
                        name="raname"
                        onChange={e => handleChange(e)}
                        style={{ width: '35%' }}
                      >
                        {outputNLP.map((item) => (
                          <Option
                            key={item.uid}
                            value={item.hint}
                          >
                            {item.hint}
                          </Option>
                        ))}
                      </Select>
                    </Form.Item>

                    <Form.Item label="SELECT ARTICLE NAME AND ID">
                      <Row >
                        <Col span={9}>
                          <Select
                            size="large"
                            placeholder={"Title of the Article"}
                            style={{ width: '93%' }}
                            onChange={onChange}
                          >
                            {selectedArticles.articles.map((item) => (
                              <Option
                                key={item.id}
                                value={item.article}
                              >
                                {item.article}
                              </Option>
                            ))}
                          </Select>
                        </Col>
                        <Col span={2} >
                          <Input
                            size="large"
                            style={{ width: '100%' }}
                            placeholder={"ID"}
                            value={addArticle.articleNo}
                          />
                        </Col>
                        <Col span={13} ></Col>
                      </Row>
                    </Form.Item>

                    <Form.Item label="SELECT VARIABLES">
                      {selectedArticle.variables.map((item) => (
                        <Row >
                          <Col span={4}>
                            <Input
                              size="large"
                              style={{ width: '85%' }}
                              placeholder={"ID"}
                              defaultValue={item.verify}
                              type="text"
                            />
                          </Col>
                          <Col span={5}>
                            <Input
                              name="value"
                              size="large"
                              placeholder={"Value"}
                              style={{ width: '88%' }}
                              type="text"
                            //value={element.value || ""}
                            //onChange={e => handleVariablesChange(index, e)}
                            />
                          </Col>
                          <Col span={15}></Col>
                        </Row>
                      ))}
                    </Form.Item>

                    <Form.Item label="ENABLE CUSTOM TEXTS">
                      <Col span={20}>
                        <Switch
                          size="large"
                          onChange={e => handleInput(e)}
                        />
                        <TextArea
                          placeholder={"Name of the Roaming Agreement"}
                          name="customText"
                          size="large"
                          rows={4}
                          style={{ width: '99%' }}
                          disabled={input.value}
                          type="text"
                          onChange={handleChangeRA}
                        />
                      </Col>
                      <Col span={4}></Col>
                    </Form.Item>

                    <Form.Item>
                      <br />
                      <Button
                        size="large"
                        type="primary"
                        htmlType="submit"
                        block
                        style={{ width: '40.5%' }}
                      >
                        PROPOSE ARTICLE
                      </Button>
                    </Form.Item>
                  </Row>
                </Form.Item>
              </Col>
            </Spin>
          </Form>
        </Col>
      </Row>
    </section >
  );
};

export default AddArticle;
