import React, { useState } from "react";
import {
  AutoComplete,
  Row,
  Col,
  Form,
  Input,
  Button,
  Select,
  Spin,
  notification,
  Tooltip
} from "antd";
import "./../../App.css";
import axios from "axios";
import { Icon as NewIco } from "antd";
import Clipboard from 'react-clipboard.js';
import { useGlobal } from "reactn";
import * as outputNLP from "./../../data/outputNLP.json";

const { Option } = Select;
const initialFormState = {
  raname: "",
  articleNo: "",
  articleName: "",
};

const AddArticle = () => {

  const formItemLayout = {};
  const [global] = useGlobal();
  const [loading, setLoading] = useState(false);
  let userDetails = JSON.parse(localStorage.getItem('user'));
  const [addArticle, setAddArticle] = useState(initialFormState);


  const openNotificationWithIcon = (type, title, description) => {
    notification[type]({
      message: title,
      description: description,
    });
  };

  const onClick = () => {
    const value = global.value;
    addArticle.raname = value;
    setAddArticle(prevValue => ({ ...prevValue, raname: value }));
  }

  function handleChange(event) {
    const value = event.target.value;
    console.log(value);
    setAddArticle({
      ...addArticle,
      [event.target.name]: value
    });
  }

  const onChange = (value) => {
    setAddArticle({ ...addArticle, articleNo: value })
    addArticle.articleNo = value;
    outputNLP.NLP.map(item => {
      item.variations.map(data => {
        if (data.id == value) {
          setAddArticle({ ...addArticle, articleName: data.article })
          addArticle.articleName = data.article;
        }
      })
    })
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true)
    const jwtToken = localStorage.getItem("token");
    axios
      .post(`http://${process.env.REACT_APP_GATEWAY_HOST}:${process.env.REACT_APP_GATEWAY_PORT}/proposeAddArticle`, { addArticle, userDetails }, { headers: { "Authorization": `Bearer ${jwtToken}` } })
      .then((res) => {
        console.log(addArticle);
        if (res.status === 200) {
          openNotificationWithIcon(
            "success",
            "SUCCESSFULLY REGISTERED AGREEMENT"
          );
        }
        if (res.status === 201) {
          openNotificationWithIcon(
            "error",
            "MISSING VALUES TO CREATE THE AGREEMENT"
          );
        }
        if (res.status === 202) {
          openNotificationWithIcon(
            "error",
            "ROAMING AGREEMENT MUST BE CREATED BETWEEN TWO MNOs"
          );
        }
      })
      .catch(() =>
        openNotificationWithIcon(
          "error",
          "UNREGISTERED ROAMING AGREEMENT",
        )
      )
      .finally(() => setLoading(false));
  };

  return (
    <section className="CommentsWrapper">
      <h2> PROPOSE ARTICLES FOR ROAMING AGREEMENT</h2>
      <Row gutter={[16, 16]} type="flex">
        <Col xl={24} lg={24} md={24}>
          <Form
            {...formItemLayout}
            name="basic"
            initialvalues={{
              remember: true
            }}
            onSubmit={handleSubmit}
          >
            <Spin spinning={loading}>
              <Col lg={1} md={24}></Col>
              <Col lg={23} md={24}>
                <Form.Item
                  label="NAME OF THE ROAMING AGREEMENT"
                  name="raname"
                  rules={[
                    {
                      required: true,
                      message: "NAME OF THE ROAMING AGREEMENT"
                    }
                  ]}
                >
                  <Input
                    size="large"
                    placeholder={"Name of the Roaming Agreement"}
                    suffix={
                      <Clipboard onClick={onClick} style={{ background: 'white', border: '0px', outline: '0px' }}>
                        <Tooltip title="Paste raname Name">
                          <NewIco type="snippets" style={{ color: 'black', fontSize: 'x-large' }} />
                        </Tooltip>
                      </Clipboard>
                    }
                    type="text"
                    name="raname"
                    value={addArticle.raname}
                    onChange={handleChange}
                    style={{ width: '35%' }}
                  />
                </Form.Item>

                <Form.Item hasFeedback>
                  {outputNLP.NLP.map((item, index) => (
                    <Row>
                      <Form.Item
                        label="SELECT ARTICLE ID AND NAME"
                      >
                        <Col span={2} >
                          <AutoComplete
                            size="large"
                            dataSource={item.variations.map(data => data.id)}
                            placeholder={"ID"}
                            onSelect={(data) => data}
                            onChange={onChange}
                            style={{ width: '75%' }}
                          />
                        </Col>
                        <Col span={14}>
                          <Input
                            size="large"
                            style={{ width: '46%' }}
                            placeholder={"Name of the Article"}
                            defaultValue={addArticle.articleName}
                            value={addArticle.articleName}
                          />
                        </Col>
                        <Col span={8} ></Col>
                      </Form.Item>
                      <Form.Item
                        label="SELECT VARIABLES"
                      >
                        <Row type="flex" justify="start" gutter={16} key={index}>
                          <Col xs={4} sm={4} xl={4} >
                            <AutoComplete
                              name="feature"
                              size="large"
                              data-id={index}
                              style={{ width: '100%' }}
                              type="text"
                            />
                          </Col>
                          <Col xs={2} sm={2} xl={2}>
                            <Input
                              name="price"
                              size="large"
                              placeholder={"ID"}
                              style={{ width: '100%' }}
                              data-id={index}
                              type="number"
                            />
                          </Col>
                          <Col xs={5} sm={5} xl={5}>
                            <Input
                              name="name"
                              size="large"
                              data-id={index}
                              style={{ width: '100%' }}
                              type="text"
                            />
                          </Col>
                        </Row>
                      </Form.Item>
                    </Row>
                  ))}
                </Form.Item>

                <Form.Item>
                  <br />
                  <Button
                    size="large"
                    type="primary"
                    htmlType="submit"
                    block
                    style={{ width: '35%' }}
                  >
                    PROPOSE ARTICLE
                  </Button>
                </Form.Item>
              </Col>
            </Spin>
          </Form>
        </Col>
      </Row>
    </section >
  );
};

export default AddArticle;
