import React, { useState } from "react";
import {
  AutoComplete,
  Row,
  Col,
  Form,
  Input,
  InputNumber,
  Button,
  Spin,
  notification,
  Tabs,
  Tooltip
} from "antd";
import "./../../App.css";
import axios from "axios";
import { Icon as NewIco } from "antd";
import Clipboard from 'react-clipboard.js';
import { useGlobal } from "reactn";

notification.config({
  placement: "topRight",
  bottom: 50,
  duration: 1.5,
});

const AddArticle = () => {

  const dataSource = ['Variable', 'Variation', 'Standard Clause', 'Custom Text'];

  const formItemLayout = {};
  const [global] = useGlobal();
  const initialFormState = {
    raname: "",
    articleNo: "",
    variables: "",
    variations: "",
    customTexts: "",
    standardClauses: "",
    rows: [{}]
  };

  const [rows, setRows] = useState(initialFormState)
  const [loading, setLoading] = useState(false);
  let userDetails = JSON.parse(localStorage.getItem('user'));
  const [addArticle, setAddArticle] = useState(initialFormState);
  const [idx, setIdx] = useState(initialFormState);

  function handleRemoveSpecificRow(idx) {
    //const rows = [...this.state.rows]
    //rows.splice(idx, 1)
    //this.setState({ rows })
  }

  const onClick = () => {
    const value = global.value;
    addArticle.raname = value;
    setAddArticle(prevValue => ({ ...prevValue, tokenid: value }));
  }

  const onChange = (value) => {
    setAddArticle({ ...addArticle, articleNo: value })
    addArticle.articleNo = value;
  };

  function handleChange(event) {
    const value = event.target.value;
    setAddArticle({
      ...addArticle,
      [event.target.name]: value
    });
  }

  //Create a news products
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true)
    const jwtToken = localStorage.getItem("token");
    //Set POST request
    axios
      .post(`http://${process.env.REACT_APP_GATEWAY_HOST}:${process.env.REACT_APP_GATEWAY_PORT}/proposeAddArticle`, { addArticle, userDetails }, { headers: { "Authorization": `Bearer ${jwtToken}` } })
      .then((res) => {
        if (res.status === 200) {
          openNotificationWithIcon(
            "success",
            "SUCCESSFULLY REGISTERED AGREEMENT"
          );
        }
        if (res.status === 201) {
          openNotificationWithIcon(
            "error",
            "MISSING VALUES TO CREATE THE AGREEMENT"
          );
        }
        if (res.status === 202) {
          openNotificationWithIcon(
            "error",
            "ROAMING AGREEMENT MUST BE CREATED BETWEEN TWO MNOs"
          );
        }
      })
      .catch(() =>
        openNotificationWithIcon(
          "error",
          "UNREGISTERED ROAMING AGREEMENT",
        )
      )
      .finally(() => setLoading(false));
  };

  const openNotificationWithIcon = (type, title, description) => {
    notification[type]({
      message: title,
      description: description,
    });
  };

  return (
    <section className="CommentsWrapper">
      <h2> PROPOSE AN ARTICLE FOR A ROAMING AGREEMENT</h2>
      <Row gutter={[16, 16]} type="flex">
        <Col xl={24} lg={24} md={24}>
          <Form
            {...formItemLayout}
            name="basic"
            initialvalues={{
              remember: true
            }}
            onSubmit={handleSubmit}
          >
            <Spin spinning={loading}>
              <Col lg={24} md={24}>

                <Form.Item
                  label="NAME OF THE ROAMING AGREEMENT"
                  name="raname"
                  rules={[
                    {
                      required: true,
                      message: "NAME OF THE ROAMING AGREEMENT"
                    }
                  ]}
                >
                  <Input
                    size="large"
                    placeholder={"Paste the Name of the Roaming Agreement"}
                    suffix={
                      <Clipboard onClick={onClick} style={{ background: 'white', border: '0px', outline: '0px' }}>
                        <Tooltip title="Paste raname Name">
                          <NewIco type="snippets" style={{ color: 'black', fontSize: 'x-large' }} />
                        </Tooltip>
                      </Clipboard>
                    }
                    type="text"
                    name="raname"
                    value={addArticle.raname}
                    onChange={handleChange}
                    style={{ width: '35%' }}
                  />
                </Form.Item>

                <Form.Item
                  label="ARTICLE NUMBER:"
                  name="articleNo"
                  rules={[
                    {
                      required: true,
                      message: "Introduce the article number"
                    }
                  ]}
                >
                  <InputNumber
                    size="large"
                    placeholder={"1"}
                    min={1}
                    max={100000}
                    name="articleNo"
                    value={addArticle.articleNo}
                    onChange={onChange}
                    style={{ width: '10%' }}
                  />
                </Form.Item>

                <Form.Item
                  label="ARTICLE CONTENT:"
                  name="articleNo"
                  rules={[
                    {
                      required: true,
                      message: "Introduce the article number"
                    }
                  ]}
                >

                  <Row type="flex" justify="start" gutter={16}>
                    <Col xs={3} sm={3} xl={3} >
                      <AutoComplete
                        size="large"
                        style={{ width: '100%' }}
                        dataSource={dataSource}
                        placeholder={"Feature"}
                        filterOption={(inputValue, option) =>
                          option.props.children.toUpperCase().indexOf(inputValue.toUpperCase()) !== -1
                        }
                      />
                    </Col>
                    <Col xs={2} sm={2} xl={2} >
                      <InputNumber
                        size="large"
                        placeholder={"ID"}
                        min={1}
                        max={100000}
                        name="articleNo"
                        value={addArticle.articleNo}
                        onChange={onChange}
                        style={{ width: '100%' }}
                      />
                    </Col>
                    <Col xs={8} sm={8} xl={8} >
                      <Input
                        size="large"
                        placeholder={"Value"}
                        type="text"
                        name="raname"
                        value={addArticle.raname}
                        onChange={handleChange}
                        style={{ width: '100%' }}
                      />
                    </Col>
                    <Col xs={1} sm={1} xl={1} >
                      <Button
                        type="primary"
                        icon="plus"
                        style={{ width: '100%' }}
                        size="large"
                      >
                      </Button>
                    </Col>
                    <Col xs={1} sm={1} xl={1} >
                      <Button
                        type="primary"
                        icon="minus"
                        style={{ width: '100%' }}
                        size="large"
                        onClick={handleRemoveSpecificRow(idx)}

                      >
                      </Button>
                    </Col>
                  </Row>

                </Form.Item>

                <br />
                <br />
                <br />
                <Form.Item>
                  <Button
                    size="large"
                    type="primary"
                    htmlType="submit"
                    block
                    style={{ width: '35%' }}
                  >
                    PROPOSE ARTICLE
                  </Button>
                </Form.Item>
              </Col>
            </Spin>
          </Form>
        </Col>
      </Row>
    </section >
  );
};

export default AddArticle;
