import React, { useState } from "react";
import {
  AutoComplete,
  Row,
  Col,
  Form,
  Input,
  InputNumber,
  Button,
  Spin,
  notification,
  Tabs,
  Tooltip
} from "antd";
import "./../../App.css";
import axios from "axios";
import { Icon as NewIco } from "antd";
import Clipboard from 'react-clipboard.js';
import { useGlobal } from "reactn";

const _defaultCosts = [
  {
    name: "Rice",
    price: 40
  },
  {
    name: "Noodle",
    price: 20
  }
];

const AddArticle = () => {

  const formItemLayout = {};
  const [loading, setLoading] = useState(false);
  const [costs, setCosts] = useState(_defaultCosts);
  let userDetails = JSON.parse(localStorage.getItem('user'));

  const handleCostsChange = event => {
    const _tempCosts = [...costs];
    _tempCosts[event.target.dataset.id][event.target.name] = event.target.value;

    setCosts(_tempCosts);
  };

  const addNewCost = () => {
    setCosts(prevCosts => [...prevCosts, { name: "", price: 0 }]);
  };

  const getTotalCosts = () => {
    return costs.reduce((total, item) => {
      return total + Number(item.price);
    }, 0);
  };

  const openNotificationWithIcon = (type, title, description) => {
    notification[type]({
      message: title,
      description: description,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true)
    const jwtToken = localStorage.getItem("token");
    //Set POST request
    axios
      .post(`http://${process.env.REACT_APP_GATEWAY_HOST}:${process.env.REACT_APP_GATEWAY_PORT}/proposeAddArticle`, { userDetails }, { headers: { "Authorization": `Bearer ${jwtToken}` } })
      .then((res) => {
        if (res.status === 200) {
          openNotificationWithIcon(
            "success",
            "SUCCESSFULLY REGISTERED AGREEMENT"
          );
        }
        if (res.status === 201) {
          openNotificationWithIcon(
            "error",
            "MISSING VALUES TO CREATE THE AGREEMENT"
          );
        }
        if (res.status === 202) {
          openNotificationWithIcon(
            "error",
            "ROAMING AGREEMENT MUST BE CREATED BETWEEN TWO MNOs"
          );
        }
      })
      .catch(() =>
        openNotificationWithIcon(
          "error",
          "UNREGISTERED ROAMING AGREEMENT",
        )
      )
      .finally(() => setLoading(false));
  };

  return (
    <section className="CommentsWrapper">
      <h2> PROPOSE AN ARTICLE FOR A ROAMING AGREEMENT</h2>
      <Row gutter={[16, 16]} type="flex">
        <Col xl={24} lg={24} md={24}>
          <Form
            {...formItemLayout}
            name="basic"
            initialvalues={{
              remember: true
            }}
            onSubmit={handleSubmit}
          >
            <Spin spinning={loading}>
              <Col lg={24} md={24}>

                <Form.Item
                  label="NAME OF THE ROAMING AGREEMENT"
                  name="raname"
                  rules={[
                    {
                      required: true,
                      message: "NAME OF THE ROAMING AGREEMENT"
                    }
                  ]}
                >
                  <Input
                    size="large"
                    placeholder={"Paste the Name of the Roaming Agreement"}
                    suffix={
                      <Clipboard onClick={onClick} style={{ background: 'white', border: '0px', outline: '0px' }}>
                        <Tooltip title="Paste raname Name">
                          <NewIco type="snippets" style={{ color: 'black', fontSize: 'x-large' }} />
                        </Tooltip>
                      </Clipboard>
                    }
                    type="text"
                    name="raname"
                    value={addArticle.raname}
                    onChange={handleChange}
                    style={{ width: '35%' }}
                  />
                </Form.Item>

                <Form.Item
                  label="ARTICLE NUMBER:"
                  name="articleNo"
                  rules={[
                    {
                      required: true,
                      message: "Introduce the article number"
                    }
                  ]}
                >
                  <InputNumber
                    size="large"
                    placeholder={"1"}
                    min={1}
                    max={100000}
                    name="articleNo"
                    value={addArticle.articleNo}
                    onChange={onChange}
                    style={{ width: '10%' }}
                  />
                </Form.Item>
                <div className="table">
                  <div className="table-title">Food costs</div>
                  <div className="table-content">
                    <div className="table-header">
                      <div className="table-row">
                        <div className="table-data">
                          <div>Item</div>
                        </div>
                        <div className="table-data">
                          <div>Price</div>
                        </div>
                      </div>
                    </div>
                    <div className="table-body">
                      {costs.map((item, index) => (
                        <div className="table-row" key={index}>
                          <div className="table-data">
                            <input
                              name="name"
                              data-id={index}
                              type="text"
                              value={item.name}
                              onChange={handleCostsChange}
                            />
                          </div>
                          <div className="table-data">
                            <input
                              name="price"
                              data-id={index}
                              type="number"
                              value={item.price}
                              onChange={handleCostsChange}
                            />
                          </div>
                        </div>
                      ))}
                      <div className="table-row">
                        <div className="table-data">
                          <button onClick={addNewCost}>+</button>
                        </div>
                      </div>
                    </div>
                    <div className="table-footer">
                      <div className="table-row">
                        <div className="table-data">
                          <div>Total</div>
                        </div>
                        <div className="table-data">
                          <div>{getTotalCosts()}</div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                <Form.Item>
                  <Button
                    size="large"
                    type="primary"
                    htmlType="submit"
                    block
                    style={{ width: '35%' }}
                  >
                    PROPOSE ARTICLE
                  </Button>
                </Form.Item>
              </Col>
            </Spin>
          </Form>
        </Col>
      </Row>
    </section >
  );
};

export default AddArticle;
